public class ShortestPalindrom {
    public static void main(String[] args) {
        ShortestPalindrom shortestPalindrom = new ShortestPalindrom();
        System.out.println(shortestPalindrom.shortestPalindrome(""));
    }

    public String shortestPalindrome(String s) {
        if (s == null || s.length() <= 1) return s;

        int i = s.length() - 1;
        for (; i >= 0; i--) {
            if (isPalindrome(s, 0, i)) {
                break;
            }
        }

        if (i == s.length() - 1) {
            return s;
        }

        String suffix = s.substring(i + 1);
        String prefix = new StringBuffer(suffix).reverse().toString();
        return prefix + s.substring(0, i + 1) + suffix;
    }

    private boolean isPalindrome(String s, int start, int end) {
        while (start < end) {
            if (s.charAt(start) == s.charAt(end)) {
                start++;
                end--;
            } else {
                return false;
            }
        }

        return true;
    }

}
